package cz.mendelu.genetika.genoms.helpers

import cz.mendelu.genetika.genoms.Feature
import cz.mendelu.genetika.genoms.Sequence
import cz.mendelu.genetika.palindrome.CompositePalindromeDetector
import cz.mendelu.genetika.palindrome.Palindrome
import cz.mendelu.genetika.palindrome.PalindromeDetector
import cz.mendelu.genetika.palindrome.PalindromeMatcher

/**
 * Created by Jiří Lýsek on 14.12.2016.
 */
class FeatureSieveTest extends GroovyTestCase {

    private static final String INSTANCE = FeatureSieveTest.class.simpleName;

    void setUp() {

    }

    void tearDown() {

    }

    void "test sieving of features"() {
        Sequence s = new Sequence("".bytes);

        SortedSet size = (6..30) as SortedSet;
        SortedSet spacer = (0..10) as SortedSet;
        SortedSet mismatches = (0..1) as SortedSet;
        PalindromeDetector detector = CompositePalindromeDetector.palindromeDetectorBuilder().getPalindromeDetector(size, spacer, mismatches);

        PalindromeMatcher palindromes = detector.findPalindrome(s);

        assert palindromes.getCount() == 96;

        Palindrome place = new Palindrome(s, 500, 10, 0, 0);

        FeatureSieve<Palindrome> sieve = new FeatureSieve<Palindrome>(place);
        sieve.addRange(0);
        sieve.addRange(20);
        sieve.addRange(100);
        palindromes.stream().forEach({ p ->
            sieve.decideMembership(p);
        });
        assert sieve.getCount() == 0;
        assert sieve.getCount(20) == 2;
        assert sieve.getCount(100) == 9;
    }

    void "test features before and after"() {
        Sequence s = new Sequence("".bytes);

        Feature feature = new Feature("Static", 200, 220, Feature.FORWARD);
        Palindrome palindromeAfter1 = new Palindrome(s, 225, 5, 0, 0);
        Palindrome palindromeAfter2 = new Palindrome(s, 245, 10, 0, 0);
        Palindrome palindromeBefore = new Palindrome(s, 190, 8, 0, 0);

        FeatureSieve<Palindrome> sieve = new FeatureSieve<Palindrome>(feature);
        sieve.addRange(0);
        sieve.addRange(20);
        sieve.addRange(100);
        sieve.decideMembership(palindromeAfter1);
        sieve.decideMembership(palindromeAfter2);
        sieve.decideMembership(palindromeBefore);

        assert sieve.getCount(0) == 0;
        assert sieve.getCount(20) == 2;
        assert sieve.getCount(100) == 1;
        assert sieve.getCountAfter(20) == 1;
        assert sieve.getCountBefore(20) == 1;
        assert sieve.getCountAfter(100) == 1;
        assert sieve.getCountBefore(100) == 0;
    }

}
