package cz.mendelu.genetika.binding.predictor

import cz.mendelu.genetika.genoms.Sequence

import java.util.stream.Stream

class P53BindingPredictorTest extends GroovyTestCase {

    private static final String INSTANCE = P53BindingPredictorTest.class.simpleName;

    void setUp() {

    }

    void tearDown() {

    }

    void "test best prediction"() {
        P53BindingPredictor predictor = new P53BindingPredictor();
        double result = predictor.calcForSequence("GGACATGCCCGGGCATGTCC");
        assert result == -7.61;
    }

    void "test some prediction"() {
        P53BindingPredictor predictor = new P53BindingPredictor();
        double result = predictor.calcForSequence("CACTGTACCTCATGCCTTAG");
        assert result == -3.38;
    }

    void "test too short"() {
        P53BindingPredictor predictor = new P53BindingPredictor();
        String message = shouldFail {
            predictor.calcForSequence("CACTGTACCTCATGC");
        }
        assert message == "Sequence length must be 20."
    }

    void "test find best"() {
        P53BindingPredictor predictor = new P53BindingPredictor(2);
        Sequence s = new Sequence("".bytes);
        Stream<BindingPrediction> result = predictor.find(s);
        assert result.collect().size() == 7;
    }

    void "test one find best"() {
        P53BindingPredictor predictor = new P53BindingPredictor(2);
        Sequence s = new Sequence("".bytes);
        Stream<BindingPrediction> result = predictor.find(s);
        assert result.filter({ item ->
            if(item.getPosition() == 2361 && item.getSize() == 20 && item.getSequence().toString().equals("TTACATGATCCCCCATGTTG")) {
                return true;
            } else {
                return false;
            }
        }).collect().size() == 1;
    }

    void "test scan"() {
        P53BindingPredictor predictor = new P53BindingPredictor(2);
        Sequence s = new Sequence("".bytes);
        double[] result = predictor.scan(s);
        assert result.size() == s.getLength() - 19;
    }

}