package cz.mendelu.genetika.genoms.helpers

import cz.mendelu.genetika.binding.predictor.BindingPrediction
import cz.mendelu.genetika.genoms.Sequence

/**
 * Created by Jiří Lýsek on 16.12.2016.
 */
class SequenceHelperClass extends GroovyTestCase {

    private static final String INSTANCE = SequenceHelperClass.class.simpleName;

    void setUp() {

    }

    void tearDown() {

    }

    void "test range"() {
        Sequence s = new Sequence("".bytes);
        BindingPrediction bp1 = new BindingPrediction(s, 100, 500, 0);
        BindingPrediction bp2 = new BindingPrediction(s, 80, 10, 0);

        assert SequenceHelper.isInRange(bp1, bp2, 30) == true;
        assert SequenceHelper.isInRange(bp2, bp1, 30) == false;
    }

    void "test range with offset"() {
        Sequence s = new Sequence("".bytes);
        BindingPrediction bp1 = new BindingPrediction(s, 200, 100, 0);
        BindingPrediction bp2 = new BindingPrediction(s, 80, 20, 0);

        assert SequenceHelper.isInRange(bp1, bp2, 100) == false;
        assert SequenceHelper.isInRange(bp1, bp2, 100, 100) == true;
    }

}
